import { Schema, model } from 'mongoose';

const user = new Schema({
  name: {
    type: String,
    required: true,
  },
  email: {
    type: String,
    required: true,
    unique: true,
  },
  password: {
    type: String,
    required: true,
  },
  cv: {
    type: String,
    required: false,
  },
  bio: {
    type: String,
    required: false,
  },
  profile_picture: {
    type: String,
    default: 'data:image/png;base64,',
    required: false,
  },
  level: {
    type: String,
    enum: ['JUNIOR', 'MIDDLE', 'SENIOR', 'EXPERT', 'INTERNSHIP'],
    required: false,
  },
  role: {
    type: String,
    required: true,
    enum: ['admin', 'interviewer', 'interviewee'],
  },
});

const User = model('User', user);

export default User;
